def bot_listener(bot_server: socket.socket) -> None:
    global bots # Access to the 'bots' variable outside of this function
    while True:
        try: bot_client, addr = bot_server.accept() # Accept connection
        except: print("[ X ] Error, Failed to accept bot connection"); continue # Print socket failure message

        bots.append(bot_client) ## Append//Add bot's socket to the bots list
        threading.Thread(target=bot_handler, args=(bot_client,addr,)).start() # Start bot handler
        

